export const cells = [
    {
        name: 'Name',
        key: 'name',
        type: 'text',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Category',
        key: 'category',
        type: 'text',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Description',
        key: 'description',
        type: 'text',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Weight',
        key: 'weight',
        type: 'number',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Diet',
        key: 'diet',
        type: 'text',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Price',
        key: 'price',
        type: 'number',
        render: function(row) {
            return createSpan(row[this.key]);
        }
    },
    {
        name: 'Controls',
        key: 'controls',
        render: (row) => {
            let buttonsContainer = document.createElement("div");
            let btn1 = createButton("Edit", handleEditSaveBtnClick, null, row.id);
            let btn2 = createButton("Delete", handleDeleteCancelBtnClick, null, row.id);
            buttonsContainer.appendChild(btn1);
            buttonsContainer.appendChild(btn2);
            return buttonsContainer;
        }
    }
];
